// Process and filter Genesis-managed workflows
const workflows = $input.first().json.data || [];
const genesisWorkflows = [];

for (const workflow of workflows) {
  // Check if Genesis-managed
  if (workflow.meta?.genesisVersion || 
      workflow.meta?.genesisSpawned || 
      workflow.name?.startsWith('GENESIS_') ||
      workflow.tags?.includes('genesis')) {

    genesisWorkflows.push({
      id: workflow.id,
      name: workflow.name,
      active: workflow.active,
      createdAt: workflow.createdAt,
      updatedAt: workflow.updatedAt,
      meta: workflow.meta || {},
      nodeCount: workflow.nodes?.length || 0
    });
  }
}

return [{
  totalWorkflows: workflows.length,
  genesisWorkflows: genesisWorkflows.length,
  workflows: genesisWorkflows,
  checkTime: new Date().toISOString()
}];

// Analyze workflow health
const workflow = $('Batch Health Checks').item.json.workflows[0];
const executions = $input.first().json.data || [];

// Calculate health metrics
const now = new Date();
const healthCheck = {
  workflowId: workflow.id,
  workflowName: workflow.name,
  isActive: workflow.active,
  lastExecution: null,
  recentExecutions: executions.length,
  errorCount: 0,
  successCount: 0,
  errorRate: 0,
  avgExecutionTime: 0,
  lastError: null,
  healthStatus: 'healthy',
  issues: []
};

// Analyze executions
let totalTime = 0;
for (const exec of executions) {
  if (exec.finished) {
    const startTime = new Date(exec.startedAt);
    const endTime = new Date(exec.stoppedAt);
    totalTime += (endTime - startTime) / 1000;

    if (exec.status === 'error' || exec.status === 'crashed') {
      healthCheck.errorCount++;
      if (!healthCheck.lastError) {
        healthCheck.lastError = {
          time: exec.stoppedAt,
          error: exec.data?.error?.message || 'Unknown error'
        };
      }
    } else if (exec.status === 'success') {
      healthCheck.successCount++;
    }
  }

  if (!healthCheck.lastExecution || new Date(exec.startedAt) > new Date(healthCheck.lastExecution)) {
    healthCheck.lastExecution = exec.startedAt;
  }
}

// Calculate metrics
if (executions.length > 0) {
  healthCheck.errorRate = healthCheck.errorCount / executions.length;
  healthCheck.avgExecutionTime = totalTime / executions.length;
}

// Determine health status
const hoursSinceLastExec = healthCheck.lastExecution ? 
  (now - new Date(healthCheck.lastExecution)) / (1000 * 60 * 60) : 999;

// Health rules
if (!workflow.active) {
  healthCheck.healthStatus = 'inactive';
  healthCheck.issues.push('Workflow is not active');
} else if (healthCheck.errorRate > 0.5) {
  healthCheck.healthStatus = 'critical';
  healthCheck.issues.push(`High error rate: ${(healthCheck.errorRate * 100).toFixed(1)}%`);
} else if (healthCheck.errorRate > 0.1) {
  healthCheck.healthStatus = 'warning';
  healthCheck.issues.push(`Elevated error rate: ${(healthCheck.errorRate * 100).toFixed(1)}%`);
} else if (hoursSinceLastExec > 24 && workflow.name.includes('DAILY')) {
  healthCheck.healthStatus = 'warning';
  healthCheck.issues.push('No execution in 24 hours for daily workflow');
} else if (healthCheck.avgExecutionTime > 300) {
  healthCheck.healthStatus = 'warning';
  healthCheck.issues.push(`Slow execution time: ${healthCheck.avgExecutionTime.toFixed(1)}s`);
}

return [healthCheck];

// Generate observer report
const healthChecks = $input.first().json.healthCheck || [];
const report = {
  timestamp: new Date().toISOString(),
  totalMonitored: healthChecks.length,
  healthy: 0,
  warning: 0,
  critical: 0,
  inactive: 0,
  summary: []
};

// Count statuses
for (const check of healthChecks) {
  switch(check.healthStatus) {
    case 'healthy': report.healthy++; break;
    case 'warning': report.warning++; break;
    case 'critical': report.critical++; break;
    case 'inactive': report.inactive++; break;
  }

  if (check.healthStatus !== 'healthy') {
    report.summary.push({
      workflow: check.workflowName,
      status: check.healthStatus,
      issues: check.issues
    });
  }
}

report.overallHealth = report.critical === 0 && report.warning < 3 ? 'good' : 
                      report.critical > 0 ? 'critical' : 'warning';

return [report];

// Handle manual observer requests (registration, deregistration, etc.)
const action = $input.first().json.action || 'register';
const response = {
  action,
  timestamp: new Date().toISOString(),
  success: true
};

switch(action) {
  case 'register':
    // Register new workflow for monitoring
    response.message = `Workflow ${$input.first().json.workflowId} registered for monitoring`;
    response.monitoringConfig = {
      workflowId: $input.first().json.workflowId,
      workflowName: $input.first().json.workflowName,
      monitoringLevel: $input.first().json.monitoringLevel || 'standard',
      healingEnabled: $input.first().json.healingEnabled !== false,
      alertThreshold: $input.first().json.alertThreshold || 0.9
    };
    break;

  case 'deregister':
    response.message = `Workflow ${$input.first().json.workflowId} removed from monitoring`;
    break;

  case 'status':
    response.message = 'Observer is active and monitoring';
    break;

  default:
    response.success = false;
    response.message = `Unknown action: ${action}`;
}

return [response];
